class Low_libraries {

	public function kdu() {
		$var = "ABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789";
	    srand((double)microtime()*1000000);
	    $i = 0; $rand_Code = '';
	    while ($i <= 6) {
	        $num = rand() % 33;
	        $tmp = substr($var, $num, 1);
	        $rand_Code = $rand_Code.$tmp;
	        $i++;
	    }
	    return $rand_Code;
	}

	public function iAdd($latitude, $longitude) {
	    if(!empty($latitude) && !empty($longitude)) {
	        //Send request and receive json data by address
	        $geocodeFromLatLong = file_get_contents('http://maps.googleapis.com/maps/api/geocode/json?latlng='.trim($latitude).','.trim($longitude).'&key=AIzaSyDtezUGGbDDzTEIpZANMjFT28As815T_f4'); 
	        $output = json_decode($geocodeFromLatLong);
	        $status = $output->status;
	        //Get address from json data
	        $address = ($status=="OK") ? $output->results[0]->formatted_address : '';
	        //Return address of the given latitude and longitude
	        return (!empty($address)) ? $address : false;
	    } else { return false; }
	}

